  <script>
    const toggles = document.querySelectorAll('.blog-toggle');

    toggles.forEach(btn => {
      btn.addEventListener('click', () => {
        const content = btn.nextElementSibling;

        // Close all other open blogs
        document.querySelectorAll('.blog-content').forEach(section => {
          if (section !== content) section.style.display = 'none';
        });

        // Toggle current blog
        content.style.display = (content.style.display === 'block') ? 'none' : 'block';
      });
    });
  </script>
